
public class Calcule {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5, b = 2;
		
		double c = 5 , d = 2;
		
		
		int iresultat = a / b;
		
		int imod = a % b;
		
				
		double dresultat = c / d;
		
		double dmod = c % d;
		
		System.out.println("Operations avec des int : " + iresultat +"    " +imod);
		System.out.println("Operation avec double : " + dresultat +"     "+ dmod);
		
	}
}